import React, { useState, useEffect } from "react";


const clientId = "3d8688b460024ef7ad2dcd6ab4982597";
const redirectUri = "http://localhost:3000/";
const scopes = ["user-read-currently-playing", "user-read-playback-state"];

const Music = () => {
  const [token, setToken] = useState(null);
  const [song, setSong] = useState(null);

  // Step 1: Redirect to Spotify login
  const login = () => {
    window.location = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&redirect_uri=${redirectUri}&scope=${scopes.join(
      "%20"
    )}`;
  };

  // Step 2: Parse token from URL hash
  useEffect(() => {
    const hash = window.location.hash;
    if (hash) {
      const tokenMatch = hash.match(/access_token=([^&]*)/);
      if (tokenMatch) {
        const accessToken = tokenMatch[1];
        setToken(accessToken);
        window.history.replaceState(null, null, " "); // Remove hash from URL
      }
    }
  }, []);

  // Step 3: Fetch currently playing song
  useEffect(() => {
    if (!token) return;

    const fetchSong = async () => {
      const res = await fetch("https://api.spotify.com/v1/me/player/currently-playing", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.status === 204) {
        setSong(null); // Nothing playing
        return;
      }

      const data = await res.json();
      setSong(data);
    };

    fetchSong();

    // Refresh every 10 seconds
    const interval = setInterval(fetchSong, 10000);
    return () => clearInterval(interval);
  }, [token]);

  // Not logged in
  if (!token)
    return (
      <div className="text-center">
        <button onClick={login} className="bg-black text-white px-4 py-2 rounded">
          Connect Spotify
        </button>
      </div>
    );

  // Nothing playing
  if (!song)
    return <p className="text-sm text-gray-500 text-center">No song currently playing</p>;

  return (
    <div className="flex items-center gap-4 border p-4 rounded bg-white shadow w-full max-w-md">
      <img
        src={song.item.album.images[1].url}
        alt="Album Cover"
        className="w-16 h-16 rounded"
      />
      <div>
        <p className="font-semibold text-black">{song.item.name}</p>
        <p className="text-sm text-gray-600">{song.item.artists[0].name}</p>
      </div>
    </div>
  );
};

export default Music;
