import React, { useEffect } from "react";
import "./Work.css";

function Work() {
  useEffect(() => {
    const elementsToAnimate = document.querySelectorAll(
      '.work-container, .work-container h2, .project-card, .project-title, .project-description, .project-tags'
    );

    const observerOptions = {
      root: null,
      rootMargin: '0px',
      threshold: 0.1,
    };

    const observer = new IntersectionObserver((entries, observer) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          // Staggered delay for project cards
          if (entry.target.classList.contains('project-card')) {
            const index = Array.from(document.querySelectorAll('.project-card')).indexOf(entry.target);
            entry.target.style.transitionDelay = `${index * 0.15}s`;
          }
          entry.target.classList.add('animate');
          observer.unobserve(entry.target);
        }
      });
    }, observerOptions);

    elementsToAnimate.forEach(el => observer.observe(el));

    // Cleanup on unmount
    return () => {
      observer.disconnect();
    };
  }, []);
   
  const projects = [
  {
    id: 1,
    title: "Project One",
    description: "This is the first project description.",
    image: "https://via.placeholder.com/400x225", // placeholder image
    link: "https://example.com/project1",
    tags: ["React", "CSS", "JavaScript"],
  },
  {
    id: 2,
    title: "Project Two",
    description: "This is the second project description.",
    image: "https://via.placeholder.com/400x225",
    link: "https://example.com/project2",
    tags: ["Node.js", "API"],
  },
  {
    id: 3,
    title: "Project Three",
    description: "This is the third project description.",
    image: "https://via.placeholder.com/400x225",
    link: "https://example.com/project3",
    tags: ["TypeScript", "Next.js"],
  },
];


  return (
    <div id="Work" className="work-container">
      <h2>My Projects</h2>
      <div className="project-grid">
        {projects.map((project) => (
          <a
            key={project.id}
            href={project.link}
            target="_blank"
            rel="noopener noreferrer"
            className="project-card"
            tabIndex={0}
          >
            <div className="project-image-wrapper">
              <img
                src={project.image}
                alt={project.title}
                className="project-image"
                loading="lazy"
              />
            </div>
            <div className="project-content">
              <h3 className="project-title">{project.title}</h3>
              <p className="project-description">{project.description}</p>
              <div className="project-tags">
                {project.tags.map((tag, i) => (
                  <span key={i} className="project-tag">
                    {tag}
                  </span>
                ))}
              </div>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
}

export default Work;
