import React, { useEffect, useState } from "react";

const clientId = "3d8688b460024ef7ad2dcd6ab4982597";
const redirectUri = "http://localhost:3000/";
const scopes = ["user-read-currently-playing", "user-read-playback-state"];

const Music = () => {
  const [token, setToken] = useState(null);
  const [song, setSong] = useState(null);

  // Function to redirect to Spotify login
  const login = () => {
    window.location = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&redirect_uri=${redirectUri}&scope=${scopes.join(
      "%20"
    )}`;
  };

  // On mount: check for token in localStorage or URL hash
  useEffect(() => {
    // Check localStorage first
    const localToken = localStorage.getItem("spotify_access_token");
    if (localToken) {
      setToken(localToken);
      return;
    }

    // Else parse token from URL hash
    const hash = window.location.hash;
    if (hash) {
      const tokenMatch = hash.match(/access_token=([^&]*)/);
      if (tokenMatch) {
        const accessToken = tokenMatch[1];
        setToken(accessToken);
        localStorage.setItem("spotify_access_token", accessToken);
        window.history.replaceState(null, null, " "); // Remove hash from URL
      }
    }
  }, []);

  // Fetch current playing song if token is set
  useEffect(() => {
    if (!token) return;

    const fetchSong = async () => {
      const res = await fetch("https://api.spotify.com/v1/me/player/currently-playing", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.status === 204) {
        setSong(null);
        return;
      }

      if (res.status === 401) {
        // Token expired or invalid - clear and ask to login again
        setToken(null);
        localStorage.removeItem("spotify_access_token");
        return;
      }

      const data = await res.json();
      setSong(data);
    };

    fetchSong();

    const interval = setInterval(fetchSong, 10000);
    return () => clearInterval(interval);
  }, [token]);

  if (!token)
    return (
      <div className="text-center">
        <button onClick={login} className="bg-black text-white px-4 py-2 rounded">
          Connect Spotify
        </button>
      </div>
    );

  if (!song)
    return <p className="text-sm text-gray-500 text-center">No song currently playing</p>;

  return (
    <div className="music-card">
      <img
        src={song.item.album.images[1].url}
        alt="Album Cover"
        className="music-album-cover"
      />
      <div className="music-details">
        <p className="music-title">{song.item.name}</p>
        <p className="music-artist">{song.item.artists[0].name}</p>
      </div>
    </div>
  );
};

export default Music;
