import React from "react";
import "./Work.css";
import img from "./PORTFOLIO.png"; 
import img2 from "./Nirvana.png"; // Importing the image for the first project
import img3 from "./sushi.png"; // Importing the image for the first project

const projects = [
  {
    id: 1,
    title: "Personal Portfolio",
    description: "My digital hub, featuring a selection of my best work and detailing my expertise.",
    // *** FIX IS HERE: Changed 'imgage' to 'image' ***
    image: img, 
    tags: ["React", "CSS", "2025"],
    link: "#",
  },
  {
    id: 2,
    title: "Sake Sushi",
    description: "The online home of Sake Sushi, presenting their exquisite dishes and restaurant information in an elegant, user-friendly interface. ",
    image: img3,
    tags: ["HTML", "TailwindCSS", "2025"],
    link: "https://krishnawat.github.io/SakeSushi/",
  },
  {
    id: 3,
    title: "NIRVANA - Tribute Website",
    description: "A dedicated online tribute to Nirvana, meticulously crafted to celebrate their music and legacy.",
    image:img2,
    tags: ["HTML", "CSS", "2025"],
    link: "https://krishnawat.github.io/Nirvana/",
  },
];
function Work() {
  // State to keep track of the active filter tag
  const [activeFilter, setActiveFilter] = useState("All");

  // Get all unique tags from your projects to create filter buttons
  const allTags = ["All", ...new Set(projects.flatMap(project => project.tags))];

  // Filter projects based on the activeFilter state
  const filteredProjects = projects.filter(project => {
    if (activeFilter === "All") {
      return true; // Show all projects if "All" is selected
    }
    // Check if the project's tags include the active filter tag
    return project.tags.includes(activeFilter);
  });

  return (
    <div id="Work" className="work-container">
      <h2>My Projects</h2>

      {/* --- Filter Buttons Section --- */}
      <div className="filter-buttons">
        {allTags.map((tag) => (
          <button
            key={tag}
            onClick={() => setActiveFilter(tag)}
            className={`filter-button ${activeFilter === tag ? "active" : ""}`}
          >
            {tag}
          </button>
        ))}
      </div>
      {/* --- End Filter Buttons Section --- */}

      <div className="project-grid">
        {filteredProjects.map((project) => ( // Use filteredProjects here
          <a
            key={project.id}
            href={project.link}
            target="_blank"
            rel="noopener noreferrer"
            className="project-card"
            tabIndex={0}
          >
            <div className="project-image-wrapper">
              <img
                src={project.image}
                alt={project.title}
                className="project-image"
                loading="lazy"
              />
            </div>
            <div className="project-content">
              <h3 className="project-title">{project.title}</h3>
              <p className="project-description">{project.description}</p>
              <div className="project-tags">
                {project.tags.map((tag, i) => (
                  <span key={i} className="project-tag">
                    {tag}
                  </span>
                ))}
              </div>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
}

export default Work;
