import React from "react";
import './About.css'; // Assuming you have a CSS file for styling

function About() {
  return (
    <div className="about-container">
      <h1 className="about-heading">About Me</h1>
      <p className="about-description">
        I am a passionate frontend developer with a focus on creating clean, intuitive, and impactful web experiences. My journey in web development has been driven by a love for design and a commitment to quality code.
      </p>
      <p className="about-description">
        With a background in computer science and years of experience in the industry, I have honed my skills in HTML, CSS, JavaScript, and various frontend frameworks. I thrive on challenges and enjoy pushing the boundaries of what's possible on the web.
      </p>
    </div>
  );
}

export default About;
