import React from "react";
import './Home.css'; // Your separate CSS file for the scrolling text

function Home() {
  return (
    <div className="relative min-h-screen overflow-hidden">
  {/* ðŸ”µ Background Scroll (Behind everything) */}
  <div className="absolute inset-0 -z-10 -rotate-12 pointer-events-none opacity-[0.05] select-none flex flex-col justify-between overflow-hidden">
    {/* Scroll lines */}
  </div>

  {/* ðŸ”µ Foreground content (z-10 ensures it's above background) */}
  <div className="container relative z-10">
    <canvas id="Home" className="canvas"></canvas>
    <div className="sub-heading">
      <p>FRONTEND DEVELOPER</p>
    </div>
    <div className="heading">
      <p>
        Clean Code. <br /> Intuitive Design. <br /> Real Impact.
      </p>
    </div>
    <div className="sub-heading2">
      Building immersive web experiences that push <br />
      the boundaries of what's possible
    </div>
    <div className="btn-group">
      <button className="vw">View Work</button>
      <button className="git">Get In Touch</button>
    </div>
  </div>
</div>
  );
}

export default Home;
