import React from "react";
import "./Edu.css";

    const learningItems = [
  {
    title: "BACHELOR OF COMPUTER APPLICATIONS",
    badge: "[Bachelor]",
    date: "2019 - 2023",
    description:
      "During my Bachelor's in Digital Experience Design at Thomas More, I gained a comprehensive understanding of digital design and development, focusing on Design, Development, and Business & Communication. The program was beginner-friendly and included practical projects. My degree equipped me with a versatile skill set and practical experience for a successful career in the digital field.",
  },
  {
    title: "Intermediate",
    badge: "[12th Grade]",
    date: "2022",
    description:
      "In high school, I learned web development (HTML, CSS, JavaScript) and delved into Java, focusing on Object-Oriented Programming (OOP). I explored advanced concepts, including database design and PHP programming. These experiences laid the foundation for my tech career.",
  },
  {
    title: "Matriculation",
    badge: "[10th Grade]",
    date: "2024",
    description:
      "The Ultimate React Course 2024: React, Next.js, Redux and More by Jonas Schmedtmann.",
  },

];

function Edu() {
  return (
    <div className="learning-wrapper" id="learning">
      <div className="learning-label">âœ¦ My learning curves</div>
      <div className="learning-list">
        {learningItems.map((item, idx) => (
          <div key={idx} className="learning-item">
            <div className="learning-header">
              <p>
                {item.title}{" "}
                <span className="learning-badge">{item.badge}</span>
              </p>
              <span className="learning-date">{item.date}</span>
            </div>
            <div className="learning-description">{item.description}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Edu;
